
// CREA UN PROGRAMA QUE CALCULE EL AREA DE UN CIRCULO QUE TIENE 10 METROS DE DIAMETRO
// AREA = PIr^2

# include <stdio.h>

int main (){
    // int diametro = 10;
    double pi = 3.1416;
    int radio = 5;
    double area; 
    
    area = pi * radio * radio;

    printf("El area del circulo es: %f", area);
}

    